        public void SendEmail(string Destinatario, string Asunto, string Body)
        {
            string correoSMTP = "jcalvo60304@ufide.ac.cr";
            string claveSMTP = "H0nd@c1v1c49!!49";

            MailMessage msg = new MailMessage();
            msg.To.Add(new MailAddress(Destinatario, "User"));
            msg.From = new MailAddress(correoSMTP, "Admin");
            msg.Subject = Asunto;
            msg.Body = Body;
            msg.IsBodyHtml = true;

            SmtpClient client = new SmtpClient();
            client.UseDefaultCredentials = false;
            client.Credentials = new System.Net.NetworkCredential(correoSMTP, claveSMTP);
            client.Port = 587;
            client.Host = "smtp.office365.com";
            client.DeliveryMethod = SmtpDeliveryMethod.Network;
            client.EnableSsl = true;
            client.Send(msg);
        }
    }
